import React, { useState } from "react";
import { useHistory } from 'react-router-dom';

export default function Login(e) {
    const [credentials, setCredentials] = useState({ name: "", email: "", password: "" });
    let history = useHistory();

    const handleSubmit = async () => {
        e.preventDefault();
        const response = await fetch(`http://localhost:5000/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name: credentials.name, email: credentials.email, password: credentials.password })
        });
        const json = await response.json();
        console.log(json);
        if (json.success) {
            //redirect;
            localStorage.setItem('token', json.authtoken);
            history.push("/");
        }
        else {
            alert("Invalid credentials.")
        }
    }
    const onChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value })
    }
    return <div>
        <form className="container " onSubmit={handleSubmit}>
            <div className="mb-3">
                <label htmlFor="name" value={credentials.name} className="form-label">Name</label>
                <input type="name" className="form-control" id="name" aria-describedby="emailHelp" />
            </div>
            <div className="mb-3">
                <label htmlFor="email" value={credentials.email} onChange={onChange} className="form-label">Email address</label>
                <input type="email" className="form-control" id="email" aria-describedby="emailHelp" />
                <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
            </div>
            <div className="mb-3">
                <label htmlFor="pasword" value={credentials.password} onChange={onChange} className="form-label">Password</label>
                <input type="password" className="form-control" id="pasword" />
            </div>
            <button type="submit" className="btn btn-primary"  >Submit</button>
        </form>


    </div>;
}
